package service;import dao.UserDao;import entity.User;public class UserService {    private UserDao userDao = new UserDao();    public boolean checkUserName(User user) {        if (user.getUserName().equals("") || user.getPassword().equals("")) {            return false;        }        if (userDao.checkUserNam(user.getUserName()) != null) {            return false;        }        return true;    }    public User login(User user) {        User user1 = userDao.login(user.getUserName(),user.getPassword());        if (user1!= null) {            StaticVariable.user = user1;            return user1;        }        return null;    }    public void add(User user) {        userDao.create(user);    }}